const e="big-lizard",i="Big Lizard",t="7 min",a=70,r="üêâ Big Lizard",s=[{screen_type:"hook",visual:"big_lizard_armor.png",headline:"Like Jade, but downside defined",text:"Big Lizard = Jade Lizard with a put spread (defined downside). Asymmetric wings collect bigger credit than symmetric condors."},{screen_type:"definition",headline:"Structure: Bear call spread + Bull put spread (wider put side)",text:"Sell OTM call and buy higher OTM call (bear call spread). Sell OTM put and buy lower OTM put (bull put spread). Put spread is typically wider to reflect crash risk and collect larger credit.",visual:"big_lizard_asymmetric.png",highlight_words:["Asymmetric widths","Defined risk","Bigger credit"],example:{stock:100,call_spread:{short:105,long:110,credit:2,width:5},put_spread:{short:90,long:80,credit:4,width:10},net_credit:6}},{screen_type:"analogy",headline:"Asymmetric fortress",text:"Bigger wall on the downside where crashes happen faster; smaller wall on the upside where moves are slower. Credit is higher than a symmetric condor; risk skew fits your bias.",interactive:"tap_to_reveal",reveal_sections:[{label:"Downside wider",content:"Collect more credit; accept larger potential loss"},{label:"Upside narrower",content:"Often nearly covered by credit"},{label:"Bias expression",content:"Tune widths to match your outlook"}]},{screen_type:"key_terms",headline:"Key details",cards:[{term:"Asymmetric Width",definition:"Put spread often 2√ó call spread width."},{term:"Credit vs Risk",definition:"Higher credit than condor; larger max loss on wider side."},{term:"Bias",definition:"Neutral-to-bullish setups common."},{term:"Management",definition:"Similar to condors; consider closing at 50% profit."}]},{screen_type:"multiple_choice",question:"How does a Big Lizard differ from an iron condor?",options:["Undefined risk","Asymmetric wing widths","Lower credit","Uses four expirations"],correct:1,xp_reward:10,explanation_correct:"Correct. Big Lizards skew wing widths (often wider downside) to increase credit and reflect bias.",explanation_wrong:"Big Lizards intentionally use asymmetric spread widths; condors are typically symmetric."},{screen_type:"interactive_example",headline:"Risk analysis",description:"Review max loss both sides with example numbers.",inputs:[{label:"Call Width",value:5,locked:!0},{label:"Put Width",value:10,locked:!0},{label:"Net Credit",value:6,locked:!0}],outputs:[{label:"Upside Max Loss",value:"$500 - $600 = Profit $100 (credit covers)"},{label:"Downside Max Loss",value:"$1000 - $600 = $400 loss"},{label:"Breakevens (approx)",value:"$90 - $6 = $84 (down), $105 + $6 = $111 (up)"}],teaching_points:["Asymmetry allows larger credit while keeping defined risk","Tune widths per bias and IV skew","Still follow 50% profit management guidance"]},{screen_type:"summary_cards",headline:"Quick guide",cards:[{front:"Structure",back:"Bear call spread + wider bull put spread"},{front:"Pros",back:"Bigger credit than condor; defined risk both sides"},{front:"Cons",back:"Asymmetric max loss; more complex sizing"},{front:"Use",back:"Neutral‚Äìbullish; high put IV; post-correction environments"}]},{screen_type:"celebration",xp_earned:70,total_xp:70,badge:"üêâ Big Lizard",badge_description:"You can deploy asymmetric defined-risk structures",streak_bonus:!1,celebration_message:"Asymmetric mastery achieved. Next: Ratio spreads ‚Äî caution advised.",next_lesson_preview:{title:"Ratio Spreads (Caution!)",description:"Understand unlimited risks before touching them",unlock_status:"unlocked"}}],n={lesson_id:e,title:i,estimated_time:t,total_xp:70,badge:r,screens:s};export{r as badge,n as default,t as estimated_time,e as lesson_id,s as screens,i as title,a as total_xp};
